package ch.ntb.inf.kmip.attributes;

import ch.ntb.inf.kmip.kmipenum.EnumTag;
import ch.ntb.inf.kmip.kmipenum.EnumType;
import ch.ntb.inf.kmip.objects.base.SingleAttribute;
import ch.ntb.inf.kmip.types.KMIPTextString;
import ch.ntb.inf.kmip.types.KMIPType;

public class UniqueIdentifier extends SingleAttribute<KMIPTextString> {

	public UniqueIdentifier(KMIPTextString value){
		super("Unique Identifier",
				new EnumTag(EnumTag.UniqueIdentifier),
				new EnumType(EnumType.TextString),
				value);
	}

	public UniqueIdentifier(String value) {
		this(new KMIPTextString(value));
	}

	public UniqueIdentifier(){
		this(new KMIPTextString());
	}

	public UniqueIdentifier(KMIPType value){
		this((KMIPTextString) value);
	}
}
